mapper.update:
update user
set username=#{username},
role_id=#{role.id}

    ,password=#{password}

where id = #{id}

mapper.select:
select user.*, role.id as role_id, role.name
from user,
     role
where user.role_id = role.id
  and role.id = #{id}

select user.*, role.id as role_id, role.name
from user,
     role
where user.role_id = role.id
  and username = #{username}
